import { yupResolver } from '@hookform/resolvers/yup';
import { useDispatch } from 'react-redux'
import { motion } from 'framer-motion';
import { Controller, useForm } from 'react-hook-form';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import { Link } from 'react-router-dom';
import * as yup from 'yup';
import _ from '@lodash';
import { LayOut } from 'features/common'
import 'features/user/style/UserLayout.scss'
import "features/user/style/UserRegister.scss"
import 'features/user/style/Check.scss'
import { existRequest, joinRequest } from '../reducer/userSlice';


const defaultValues = {
    username: '',
    email: '',
    phone: '',
    birth: '',
    password: '',
    address: '',
    user_interests:'',
    job:''
  
  };
export default function UserModify(){
    const { control, formState, handleSubmit, reset, getValues } = useForm({
        mode: 'onChange',
        defaultValues,
      });
      const dispatch = useDispatch()
    
      const { isValid, dirtyFields, errors } = formState;
    
      function onSubmit() {
        reset(defaultValues);
      }
    return (
        <div className="User-container">
          <motion.div
            initial={{ opacity: 0, scale: 0.6 }}
            animate={{ opacity: 1, scale: 1 }}
          >
            <Card
              square
            >
              <CardContent >
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1, transition: { delay: 0.2 } }}
                >
                  <div >
                    <div/>
                    <div>
                      <Typography className="text-24 font-semibold logo-text" color="inherit">
                      </Typography>
                      <Typography
                        class="text-16 tracking-widest -mt-8 font-700"
                        color="textSecondary"
                      >
                        회원정보 수정하기
                      </Typography>
                    </div>
                  </div>
                </motion.div>
                <form
                  name="registerForm"
                  noValidate
                  className="flex flex-col justify-center w-full"
                  onSubmit={handleSubmit(async (data) => { await dispatch(joinRequest({
                     ...data,
                    //  user_interests: document.getElementsByName('user_interests'),
                    // job:document.getElementsByName('job')
                    })) })}
                >
                  <Controller
                    name="username"
                    control={control}
                    render={({ field }) => (
                      <TextField
                        {...field}
                        className="mb-16"
                        label="Name"
                        autoFocus
                        type="username"
                        error={!!errors.username}
                        helperText={errors?.username?.message}
                        variant="outlined"
